document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("scroll-navbar");window.addEventListener("scroll",(()=>{(window.scrollY||document.documentElement.scrollTop)>0?e.classList.add("show"):e.classList.remove("show")}))})),document.addEventListener("DOMContentLoaded",(()=>{const e=document.getElementById("text-button"),t=document.getElementById("text-phone"),s=document.getElementById("call-button"),d=document.getElementById("call-phone");e.addEventListener("click",(()=>{t.classList.add("show"),e.classList.add("open"),sessionStorage.setItem("textClicked","true")})),sessionStorage.getItem("textClicked")&&(e.classList.add("open"),t.classList.add("show")),s.addEventListener("click",(()=>{d.classList.add("show"),s.classList.add("open"),sessionStorage.setItem("callClicked","true")})),sessionStorage.getItem("callClicked")&&(s.classList.add("open"),d.classList.add("show"))}));